import classes from "./Art.module.css";

import { Art as ArtType } from "../../../context/arts-context";
import { MouseEventHandler } from "react";

type ArtProps = {
  clicked: MouseEventHandler;
} & ArtType;

const art = (props: ArtProps) => {
  const onDeleteArt = () => {};

  const onCopyArt = () => {};

  const ps = 80 / (16 * 2);

  return (
    <div className={classes.Art} onClick={props.clicked}>
      <button onClick={onDeleteArt} className={classes.DeleteButton}>
        <svg
          xmlns="http://www.w3.org/2000/svg"
          class="h-6 w-6"
          fill="none"
          viewBox="0 0 24 24"
          stroke="currentColor"
        >
          <path
            stroke-linecap="round"
            stroke-linejoin="round"
            stroke-width="2"
            d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16"
          />
        </svg>
      </button>
      <button onClick={onCopyArt} className={classes.CopyButton}></button>
      {props.rows.map((row) => (
        <div className={classes.Row} key={props.rows.indexOf(row)}>
          {row.map((pix) => (
            <div
              key={pix.ypos.toString() + pix.xpos}
              style={{
                width: ps + "px",
                height: ps + "px",
                backgroundColor: pix.color,
              }}
            ></div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default art;
