import { FormEventHandler, useContext, useState } from "react";
import axios from "axios";

import { ArtsContext } from "../../context/arts-context";
import Button from "../UI/Button/Button";
import Modal from "../UI/Modal/Modal";

const UploadImage = () => {
  const { artsState, artsDispatch } = useContext(ArtsContext)!;
  const [showModal, setShowModal] = useState(false);

  const onSubmitHandler: FormEventHandler = (e) => {
    e.preventDefault();
    const formData = new FormData(e.target as HTMLFormElement);
    axios({
      method: "post",
      url: "/upl-img",
      data: formData,
      headers: { "Content-Type": "multipart/form-data" },
    })
      .then((data) => {
        artsDispatch({
          type: "ADD_ART",
          data: { id: artsState.arts.length, rows: data.data },
        });
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const onShowModal = () => {
    setShowModal(true);
  };

  const onCloseModal = () => {
    setShowModal(false);
  };

  return (
    <>
      <form
        action="/upl-img"
        method="post"
        encType="multipart/form-data"
        onSubmit={onSubmitHandler}
      >
        <input type="file" name="img" />
        <input type="hidden" value={artsState.settings.pixelSize} name="ps" />
        <input type="hidden" value={artsState.settings.defColor} name="dc" />
      </form>
      <Button type="button" clicked={onShowModal}>
        LOAD
      </Button>
      {showModal && <Modal></Modal>}
    </>
  );
};

export default UploadImage;
