import {
  FormEventHandler,
  useContext,
  useEffect,
  useRef,
  useState,
} from "react";

import classes from "./SaveImage.module.css";
import { ArtsContext } from "../../context/arts-context";
import Modal from "../UI/Modal/Modal";

const SaveImage = () => {
  const { artsState } = useContext(ArtsContext)!;
  const socket = useRef<null | WebSocket>(null);
  const [showModal, setShowModal] = useState(false);
  const [imgUrl, setImgUrl] = useState("");

  useEffect(() => {
    const s = new WebSocket("ws://localhost:3001");
    s.onopen = (e) => {
      console.log("connection");
    };
    s.onmessage = function (e) {
      const id = JSON.parse(e.data).id;
      fetch("/get-img/" + id, {
        method: "GET",
      })
        .then((res) => {
          return res.blob();
        })
        .then((data) => {
          setShowModal(true);
          const blob = new Blob([data]);
          const url = window.URL.createObjectURL(blob);
          link.href = url;
          setImgUrl(url);
          x;
        })
        .catch((err) => {
          console.log(err.message);
        });
    };
    socket.current = s;
  }, []);

  const onSaveImage: FormEventHandler = (e) => {
    e.preventDefault();
    const art = artsState.arts.find((art) => art.id === artsState.chosen)!;
    socket.current!.send(
      JSON.stringify({
        type: "CREATE_IMG",
        img: art,
        cs: artsState.settings.columnSize,
        rs: artsState.settings.rowSize,
        dc: artsState.settings.defColor,
      })
    );
  };

  const onCloseModal = () => {
    setShowModal(false);
  };

  const onDownloadImg = () => {
    const link = document.createElement("a");
    link.download = "download.png";
    link.click();
  };

  const modal = (
    <Modal closed={onCloseModal}>
      <div className={classes.ImgContainer}>
        <img src={imgUrl} alt="img-to-download" />
      </div>
      <button onClick={onDownloadImg}>DOWNLOAD</button>
    </Modal>
  );

  return (
    <form onSubmit={onSaveImage}>
      <button type="submit" className={classes.SaveImage}>
        DOWNLOAD
      </button>
      {showModal && modal}
    </form>
  );
};

export default SaveImage;
