import {
  FormEventHandler,
  useContext,
  useEffect,
  useRef,
  useState,
} from "react";
import axios from "axios";

import { ArtsContext } from "../../context/arts-context";
import Button from "../UI/Button/Button";
import Modal from "../UI/Modal/Modal";
import classes from "./UploadImage.module.css";

const UploadImage = () => {
  const { artsState, artsDispatch } = useContext(ArtsContext)!;
  const [showModal, setShowModal] = useState(false);
  const [isSelected, setIsSelected] = useState(false);
  const fileInpRef = useRef<null | HTMLInputElement>(null);
  const [haveFile, setHaveFile] = useState(false);
  const [imgIsValid, setImgIsValid] = useState(false);
  const imgRef = useRef<null | HTMLImageElement>(null);

  useEffect(() => {
    if (imgRef.current!.width) {
      validateSize();
    }
  }, [haveFile, imgRef]);

  const onSubmitHandler: FormEventHandler = (e) => {
    e.preventDefault();
    const formData = new FormData(e.target as HTMLFormElement);
    axios({
      method: "post",
      url: "/upl-img",
      data: formData,
      headers: { "Content-Type": "multipart/form-data" },
    })
      .then((data) => {
        onCloseModal();
        artsDispatch({
          type: "ADD_ART",
          data: { id: artsState.arts.length, rows: data.data },
        });
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const onShowModal = () => {
    setShowModal(true);
  };

  const onCloseModal = () => {
    setShowModal(false);
    setIsSelected(false);
    setHaveFile(false);
  };

  const onBrowseImg = () => {
    fileInpRef.current!.click();
    setIsSelected(true);
  };

  const validateSize = () => {
    console.log(
      imgRef.current?.height,
      imgRef.current?.offsetWidth,
      imgRef.current
    );
  };

  const onChosedFile = () => {
    setHaveFile(fileInpRef.current!.files!.length > 0);
  };

  return (
    <>
      <Button type="button" clicked={onShowModal}>
        LOAD
      </Button>
      {showModal && (
        <Modal closed={onCloseModal}>
          <button onClick={onBrowseImg} className={classes.BrowseButton}>
            BROWSE...
          </button>

          <form
            action="/upl-img"
            method="post"
            encType="multipart/form-data"
            onSubmit={onSubmitHandler}
          >
            <input
              onChange={onChosedFile}
              type="file"
              name="img"
              style={{ display: "none" }}
              ref={fileInpRef}
            />
            {isSelected && haveFile ? (
              <>
                <div className={classes.ImgContainer}>
                  <img
                    src={window.URL.createObjectURL(
                      fileInpRef.current!.files![0]
                    )}
                    alt=""
                    ref={imgRef}
                  />
                </div>

                <input
                  type="hidden"
                  value={artsState.settings.pixelSize}
                  name="ps"
                />
                <input
                  type="hidden"
                  value={artsState.settings.defColor}
                  name="dc"
                />
                <button
                  disabled={imgIsValid}
                  type="submit"
                  className={classes.UploadButton}
                >
                  UPLOAD
                </button>
              </>
            ) : null}
          </form>
        </Modal>
      )}
    </>
  );
};

export default UploadImage;
