import classes from "./Board.module.css";

import { SettingsContext } from "../../context/settings-context";
import { ArtsContext } from "../../context/arts-context";
import { MouseEventHandler, useContext, useRef } from "react";

import Pixel from "./Pixel/Pixel";

const Board = () => {
  const { settings, settingsDispatch } = useContext(SettingsContext);
  const { arts, artsDispatch } = useContext(ArtsContext)!;
  const boardRef = useRef(null);

  const pixelClickedHandler: MouseEventHandler<HTMLDivElement> = (e) => {
    switch (settings!.tool) {
      case "BRUSH":
    }
  };

  const board: { xpos: number; ypos: number; color: string }[][] = [];

  if (!arts!.arts) {
    for (let i = 0; i < settings!.rowSize; i++) {
      let arr = [];
      for (let j = 0; j < settings!.columnSize; j++) {
        arr.push({ xpos: j, ypos: i, color: settings!.color });
      }
      board.push(arr);
    }
    artsDispatch!({ type: "ADD_ART", data: board });
  }

  const ps = 600 / settings!.columnSize;

  return (
    <div ref={boardRef} className={classes.Board}>
      {board.map((arr) => {
        return (
          <div className={classes.BoardLine} key={board.indexOf(arr)}>
            {arr.map((pixel) => (
              <Pixel
                size={ps}
                clicked={pixelClickedHandler}
                key={pixel.ypos.toString() + pixel.xpos}
                {...pixel}
              />
            ))}
          </div>
        );
      })}
    </div>
  );
};

export default Board;
