import { useEffect, useRef } from "react";

const UploadImage = () => {
  const socket = useRef<null | WebSocket>(null);

  useEffect(() => {
    const ws = new WebSocket("ws://localhost:3001");
    socket.current = ws;
  }, []);

  const onUploadImage = () => {
    const inp = document.createElement("input");
    inp.type = "file";
    inp.click();
    inp.onchange = (e) => {
      const img = document.createElement("img");
      img.src = URL.createObjectURL((e.target as HTMLInputElement).files![0]);
      console.log(img.src);
      socket.current!.send(
        JSON.stringify({
          type: "UPL_IMG",
          data: URL.createObjectURL((e.target as HTMLInputElement).files![0]),
        })
      );
    };
  };

  return <button onClick={onUploadImage}>Load</button>;
};

export default UploadImage;
