import { useEffect, useRef } from "react";

const UploadImage = () => {
  const socket = useRef<null | WebSocket>(null);
  const formRef = useRef<null | HTMLFormElement>(null);

  useEffect(() => {
    const ws = new WebSocket("ws://localhost:3001");
    socket.current = ws;
  }, []);

  // const onUploadImage = () => {
