  //     socket.current!.send(
  //       JSON.stringify({
  //         type: "UPL_IMG",
  //         data: URL.createObjectURL((e.target as HTMLInputElement).files![0]),
  //       })
  //     );
  //   };
  // };

  const onChangeHandler = () => {
    (formRef.current as HTMLFormElement).submit();
  };

  return (
    <form action="/upl-img" method="post" ref={formRef}>
      {/* <button onClick={onUploadImage}>Load</button> */}
      <input type="file" name="img" onChange={onChangeHandler} />
    </form>
  );
};

export default UploadImage;
