import { FormEventHandler, useContext, useState } from "react";

import classes from "./SaveImage.module.css";
import { Art, ArtsContext } from "../../../context/arts-context";
import { SettingsContext } from "../../../context/settings-context";
import { createCanvas } from "canvas";

const SaveImage = () => {
  const { settings } = useContext(SettingsContext);
  const { arts } = useContext(ArtsContext)!;

  const onSaveImage: FormEventHandler = (e) => {
    e.preventDefault();
    const canvasWidth = 10 * settings!.columnSize;
    const canvasHeight = 10 * settings!.rowSize;

    const canvas = createCanvas(canvasWidth, canvasHeight);
    const context = canvas.getContext("2d");

    const art = arts.arts.find((art) => art.id === arts.chosen)!;
    for (let i = 0; i < settings!.rowSize; i++) {
      for (let j = 0; j < settings!.columnSize; j++) {
        context.fillStyle =
          art.rows[i][j].color === settings!.defColor
            ? "transparent"
            : art.rows[i][j].color;
        context.fillRect(j * 10, i * 10, 10, 10);
      }
    }
    fetch(`/api?img=${JSON.stringify(canvas)}`)
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        console.log(data);
      })
      .catch((err) => {
        console.log(err);
      });

    // canvas.toBuffer((err, result) => {
    //   if (err) {
    //     console.log(err);
    //     return;
    //   }
    //   console.log(result);
    //   fetch(`/api?img=${result}`)
    //     .then((res) => {
    //       return res.json();
    //     })
    //     .then((data) => {
    //       console.log(data);
    //     })
    //     .catch((err) => {
    //       console.log(err);
    //     });
    // }, "image/png");
  };

  return (
    <form action="/api" onSubmit={onSaveImage}>
      <button type="submit" className={classes.SaveImage}>
        SAVE
      </button>
      {/* <input type="hidden" name="img" value={JSON.stringify(img)} />
      <input type="hidden" name="cs" value={settings!.columnSize} />
      <input type="hidden" name="rs" value={settings!.rowSize} />
      <input type="hidden" name="dc" value={settings!.defColor} /> */}
    </form>
  );
};

export default SaveImage;
