import classes from "./Board.module.css";

import { SettingsContext } from "../../context/settings-context";
import { ArtsContext } from "../../context/arts-context";
import {
  DragEventHandler,
  MouseEventHandler,
  useContext,
  useEffect,
  useRef,
  useState,
} from "react";
import { Art } from "../../context/arts-context";

import Pixel from "./Pixel/Pixel";
import pixelClasses from "./Pixel/Pixel.module.css";

const Board = () => {
  const { settings, settingsDispatch } = useContext(SettingsContext);
  const { arts, artsDispatch } = useContext(ArtsContext)!;
  const [draggedPixels, setDraggedPixels] = useState<null | {
    x: number;
    y: number;
  }>(null);
  const boardRef = useRef(null);

  useEffect(() => {
    if (!arts.chosen) {
      const board: { xpos: number; ypos: number; color: string }[][] = [];
      for (let i = 0; i < settings!.rowSize; i++) {
        let arr = [];
        for (let j = 0; j < settings!.columnSize; j++) {
          arr.push({ xpos: j, ypos: i, color: settings!.defColor });
        }
        board.push(arr);
      }
      const id = Math.random().toString();
      artsDispatch({
        type: "ADD_ART",
        data: {
          rows: board,
          id: id,
        },
      });
      artsDispatch({ type: "SET_CHOSEN", data: id });
    }
  }, [artsDispatch, settings, arts]);

  const onDragEnd: EventListener = (e) => {
    e.preventDefault();
    setDraggedPixels(null);
    document.removeEventListener("dragend", onDragEnd);
  };

  const onDragStart: DragEventHandler = (e) => {
    const clone = (e.target as HTMLElement).cloneNode(true);
    (clone as HTMLElement).style.display = "none";
    e.dataTransfer.setDragImage(clone as Element, 0, 0);
    document.addEventListener("dragend", onDragEnd);
  };

  const pixelClickedHandler: MouseEventHandler = (e) => {
    e.preventDefault();
    const coords = (e.target as HTMLElement).id.split("-");
    switch (settings!.tool) {
      case "BRUSH":
        (e.target as HTMLElement).style.backgroundColor = settings!.color;
        break;
      case "ERASER":
        (e.target as HTMLElement).style.backgroundColor = settings!.defColor;
        break;
      case "FILL":
        const startColor = (e.target as HTMLElement).style.backgroundColor;
        const fillPix = (x: number, y: number) => {
          const checkPix = (xp: number, yp: number): boolean => {
            if (
              yp > settings!.rowSize - 1 ||
              yp < 0 ||
              xp > settings!.columnSize ||
              xp < 0
            )
              return false;
            const nextPix = document.getElementById(yp + "-" + xp)!;
            if (!nextPix) return false;
            const colorP = nextPix.style.backgroundColor;
            return colorP !== settings!.color && colorP === startColor;
          };
          const pix = document.getElementById(y + "-" + x)!;
          pix.style.backgroundColor = settings!.color;

          if (checkPix(x + 1, y)) {
            fillPix(x + 1, y);
          }
          if (checkPix(x, y + 1)) {
            fillPix(x, y + 1);
          }
          if (checkPix(x, y - 1)) {
            fillPix(x, y - 1);
          }
          if (checkPix(x - 1, y)) {
            fillPix(x - 1, y);
          }
        };
        fillPix(+coords[1], +coords[0]);
        break;
      case "PIPETTE":
        settingsDispatch!({
          type: "SET_COLOR",
          data: (e.target as HTMLElement).style.backgroundColor,
        });
        break;
      case "MOVE":
        let dragPix = draggedPixels;
        if (!dragPix) {
          setDraggedPixels({ x: +coords[1], y: +coords[0] });
          dragPix = { x: +coords[1], y: +coords[0] };
        }
        let x = +coords[1] - dragPix!.x;
        let y = +coords[0] - dragPix!.y;
        if (x > 0) {
          x = Math.floor(x / 2);
        } else {
          x = Math.ceil(x / 2);
        }
        if (y > 0) {
          y = Math.floor(y / 2);
        } else {
          y = Math.ceil(y / 2);
        }

        const board = document.querySelector("." + classes.Board)!;
        const pixels = board.querySelectorAll("." + pixelClasses.Pixel)!;

        if (Math.abs(x) === 1) {
          setDraggedPixels({ x: +coords[1], y: draggedPixels!.y });
          for (let i = 0; i < settings!.rowSize; i++) {
            const pix =
              pixels[
                i * settings!.columnSize + x > 0 ? settings!.columnSize - 1 : 0
              ];
            console.log(i, x);
            console.log(pix);
            pixels[
              settings!.columnSize * i + x > 0 ? 0 : settings!.columnSize
            ].insertAdjacentElement("beforebegin", pix);
          }
        }
        if (Math.abs(y) === 1) {
          setDraggedPixels({ x: draggedPixels!.x, y: +coords[0] });
          let pixs;
          if (y < 0) {
            pixs = Array.from(pixels).slice(0, settings!.columnSize).reverse();
            let prevPix = pixels[pixels.length - 1];
            for (let pix of pixs) {
              prevPix.insertAdjacentElement("afterend", pix);
            }
          } else {
            pixs = Array.from(pixels)
              .slice(
                (settings!.rowSize - 1) * settings!.columnSize,
                pixels.length - 1
              )
              .reverse();
            let prevPix = pixels[0];
            for (let pix of pixs) {
              prevPix.insertAdjacentElement("beforebegin", pix);
            }
          }
        }

        // const newArt = JSON.parse(
        //   JSON.stringify(arts.arts.find((art) => art.id === arts.chosen))
        // ) as Art;
        // newArt.rows.forEach((row, ind) => {
        //   row.forEach((pix) => {
        //     const newX = pix.xpos + x;
        //     const newY = pix.ypos + y;
        //     pix.xpos =
        //       newX > row.length - 1
        //         ? newX - row.length
        //         : newX < 0
        //         ? row.length + newX
        //         : newX;
        //     pix.ypos =
        //       newY > newArt.rows.length - 1
        //         ? newY - newArt.rows.length
        //         : newY < 0
        //         ? newArt.rows.length + newY
        //         : newY;
        //   });
        //   if (x > 0) {
        //     newArt.rows[ind] = row
        //       .slice(row.length - x)
        //       .concat(row.slice(0, row.length - x));
        //   } else {
        //     newArt.rows[ind] = row
        //       .slice(Math.abs(x))
        //       .concat(row.slice(0, Math.abs(x)));
        //   }
        // });
        // if (y > 0) {
        //   newArt.rows = newArt.rows
        //     .slice(newArt.rows.length - y)
        //     .concat(newArt.rows.slice(0, newArt.rows.length - y));
        // } else {
        //   newArt.rows = newArt.rows
        //     .slice(Math.abs(y))
        //     .concat(newArt.rows.slice(0, Math.abs(y)));
        // }
        // artsDispatch({
        //   type: "UPD_ART",
        //   data: newArt,
        //   artId: arts.chosen!,
        // });
        break;
      default:
        return;
    }
  };

  let art: null | Art = null;

  if (arts.arts.length) {
    art = arts.arts!.find((art) => art.id === arts.chosen)!;
  }

  const ps = (600 + settings!.columnSize) / settings!.columnSize;
  return (
    <div ref={boardRef} className={classes.Board}>
      {art &&
        art.rows.map((arr) => {
          return arr.map((pixel) => (
            <Pixel
              size={ps}
              clicked={pixelClickedHandler}
              dragStarted={onDragStart}
              key={pixel.ypos.toString() + pixel.xpos}
              {...pixel}
            />
          ));
        })}
    </div>
  );
};

export default Board;
