import classes from "./Pixel.module.css";

import { SettingsContext } from "../../../context/settings-context";
import { DragEventHandler, MouseEventHandler, useContext } from "react";

interface PixelProps {
  xpos: number;
  ypos: number;
  clicked: (a: number, b: number) => MouseEventHandler;
  dragStarted: DragEventHandler;
  size: number;
  color: string;
}

const Pixel = (props: PixelProps) => {
  return (
    <div
      style={{
        background: props.color,
        width: props.size,
        height: props.size,
      }}
      onDragStart={props.dragStarted}
      onClick={props.clicked(props.xpos, props.ypos)}
      className={classes.Pixel}
    ></div>
  );
};

export default Pixel;
