import { FormEventHandler, useContext, useEffect, useRef } from "react";
import axios from "axios";

import { ArtsContext } from "../../context/arts-context";

const UploadImage = () => {
  const socket = useRef<null | WebSocket>(null);
  const { artsState, artsDispatch } = useContext(ArtsContext)!;

  useEffect(() => {
    const ws = new WebSocket("ws://localhost:3001");
    socket.current = ws;
  }, []);

  const onSubmitHandler: FormEventHandler = (e) => {
    e.preventDefault();
    const formData = new FormData(e.target as HTMLFormElement);
    console.log((e.target as HTMLFormElement).img.value);
    axios({
      method: "post",
      url: "/upl-img",
      data: formData,
      headers: { "Content-Type": "multipart/form-data" },
    })
      .then((data) => {
        artsDispatch({
          type: "ADD_ART",
          data: { id: artsState.arts.length, rows: data.data },
        });
      })
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <form
      action="/upl-img"
      method="post"
      encType="multipart/form-data"
      onSubmit={onSubmitHandler}
    >
      <input type="file" name="img" />
      <input type="hidden" value={artsState.settings.pixelSize} name="ps" />
      <button type="submit">upload</button>
    </form>
  );
};

export default UploadImage;
