import React, { useReducer } from "react";

interface Pixel {
  xpos: number;
  ypos: number;
  color: string;
}

export interface Art {
  rows: Pixel[][];
  id: string;
}

interface ArtsReducerState {
  arts: Art[];
  chosen: string | null;
}

interface ArtsReducerAction {
  type: string;
  data?: any;
  artId?: string;
  x?: number;
  y?: number;
}

interface ArtsProviderValue {
  arts: ArtsReducerState;
  artsDispatch: React.Dispatch<ArtsReducerAction>;
}

const initialState: ArtsReducerState = {
  arts: [],
  chosen: null,
};

export const ArtsContext = React.createContext<ArtsProviderValue | null>(null);

interface ArtsReducer {
  (a: ArtsReducerState, b: ArtsReducerAction): ArtsReducerState;
}

const reducer: ArtsReducer = (state, action) => {
  console.log(state.arts, action.type);
  switch (action.type) {
    case "ADD_ART":
      return {
        ...state,
        arts: state.arts!.concat(action.data),
      };
    case "ADD_ROW":
      console.log(state.arts.length);
      const newArts = state.arts!.map((art) => {
        const newArt = { ...art };
        console.log(newArt, art);
        newArt.rows.push(action.data);
        console.log(newArt, art);
        return newArt;
      });
      return {
        ...state,
        arts: newArts,
      };
    case "DEL_ROW":
      console.log(state.arts);
      const arts = state.arts!.map((art) => {
        const newArt = { ...art };
        newArt.rows.pop();
        return newArt;
      });
      console.log(state.arts, arts);

      return {
        ...state,
        arts: arts,
      };
    case "ADD_COL":
      const arts1 = state.arts!.map((art) => {
        const newArt = { ...art };
        newArt.rows.forEach((row, id) => {
          row.push({ xpos: action.data.x, ypos: id, color: action.data.color });
        });
        return newArt;
      });
      return {
        ...state,
        arts: arts1,
      };
    case "DEL_COL":
      const arts2 = state.arts!.map((art) => {
        const newArt = { ...art };
        newArt.rows.forEach((row) => {
          row.pop();
        });
        return newArt;
      });
      return {
        ...state,
        arts: arts2,
      };
    case "SET_CHOSEN":
      return {
        ...state,
        chosen: action.data,
      };
    case "SET_PIX":
      let arts3 = [...state.arts];
      const a = arts3.find((art) => art.id === action.artId)!;
      console.log(a, action.artId);
      arts3[arts3.indexOf(a)].rows[action.y!][action.x!].color = action.data;
      return {
        ...state,
        arts: arts3,
      };
    default:
      throw new Error("Uncaught type: " + action.type);
  }
};

const ArtsContextProvider = (props: any) => {
  const [state, dispatch] = useReducer(reducer, initialState);

  return (
    <ArtsContext.Provider value={{ arts: state, artsDispatch: dispatch }}>
      {props.children}
    </ArtsContext.Provider>
  );
};

export default ArtsContextProvider;
