import { FormEventHandler, useContext, useRef, useState } from "react";
import axios from "axios";

import { ArtsContext } from "../../context/arts-context";
import Button from "../UI/Button/Button";
import Modal from "../UI/Modal/Modal";
import classes from "./UploadImage.module.css";

const UploadImage = () => {
  const { artsState, artsDispatch } = useContext(ArtsContext)!;
  const [showModal, setShowModal] = useState(false);
  const [isSelected, setIsSelected] = useState(false);
  const fileInpRef = useRef<null | HTMLInputElement>(null);

  const onSubmitHandler: FormEventHandler = (e) => {
    e.preventDefault();
    const formData = new FormData(e.target as HTMLFormElement);
    axios({
      method: "post",
      url: "/upl-img",
      data: formData,
      headers: { "Content-Type": "multipart/form-data" },
    })
      .then((data) => {
        artsDispatch({
          type: "ADD_ART",
          data: { id: artsState.arts.length, rows: data.data },
        });
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const onShowModal = () => {
    setShowModal(true);
  };

  const onCloseModal = () => {
    setShowModal(false);
    setIsSelected(false);
  };

  const onBrowseImg = () => {
    fileInpRef.current!.click();
    setIsSelected(true);
  };

  return (
    <>
      <Button type="button" clicked={onShowModal}>
        LOAD
      </Button>
      {showModal && (
        <Modal closed={onCloseModal}>
          <button onClick={onBrowseImg}>BROWSE</button>
          <input
            type="file"
            name="img"
            style={{ display: "none" }}
            ref={fileInpRef}
          />
          <form
            action="/upl-img"
            method="post"
            encType="multipart/form-data"
            onSubmit={onSubmitHandler}
          >
            {isSelected && fileInpRef.current!.files![0] !== null && (
              <>
                <div className={classes.ImgContainer}>
                  <img
                    src={window.URL.createObjectURL(
                      fileInpRef.current!.files![0]
                    )}
                    alt=""
                  />
                </div>

                <input
                  type="hidden"
                  value={artsState.settings.pixelSize}
                  name="ps"
                />
                <input
                  type="hidden"
                  value={artsState.settings.defColor}
                  name="dc"
                />
                <button
                  type="submit"
                  className={classes.UploadButton}
                  onClick={onSubmitHandler}
                >
                  UPLOAD
                </button>
              </>
            )}
          </form>
        </Modal>
      )}
    </>
  );
};

export default UploadImage;
