import classes from "./Art.module.css";
import Pixel from "../../Board/Pixel/Pixel";

import { Art as ArtType } from "../../../context/arts-context";
import { Pixel as PixelType } from "../../../context/arts-context";
import { MouseEventHandler } from "react";

type ArtProps = {
  clicked: MouseEventHandler;
} & ArtType;

const art = (props: ArtProps) => {
  const ps = 80 / props.rows[0].length;

  const art: PixelType[] = [];

  for (let row of props.rows) {
    row.forEach((pix) => {
      art.push(pix);
    });
  }

  return (
    <div className={classes.Art} onClick={props.clicked}>
      {art.map((pix) => (
        <div
          key={pix.ypos.toString() + pix.xpos}
          style={{
            width: ps + "px",
            height: ps + "px",
            backgroundColor: pix.color,
          }}
        ></div>
      ))}
    </div>
  );
};

export default art;
