import {
  FormEventHandler,
  MutableRefObject,
  useContext,
  useEffect,
  useRef,
  useState,
} from "react";

import classes from "./SaveImage.module.css";
import { Art, ArtsContext } from "../../../context/arts-context";
import { SettingsContext } from "../../../context/settings-context";
import { createCanvas } from "canvas";

const SaveImage = () => {
  const { settings } = useContext(SettingsContext);
  const { arts } = useContext(ArtsContext)!;
  // const [img, setImg] = useState<null | Art>(null);
  // const socket = useRef<null | WebSocket>(null);

  // useEffect(() => {
  //   const s = new WebSocket("ws://localhost:3001");
  //   s.onopen = (e) => {
  //     console.log("connection");
  //   };
  //   s.onmessage = function (e) {
  //     alert("Img is ready");
  //   };
  //   socket.current = s;
  // }, []);

  const onSaveImage: FormEventHandler = (e) => {
    // e.preventDefault();
    const img = arts.arts.find((art) => art.id === arts.chosen)!;

    const rs = settings!.rowSize;
    const cs = settings!.columnSize;

    const canvasWidth = 10 * cs;
    const canvasHeight = 10 * rs;

    const canvas = createCanvas(canvasWidth, canvasHeight);
    const context = canvas.getContext("2d");

    for (let i = 0; i < rs; i++) {
      for (let j = 0; j < cs; j++) {
        context.fillStyle =
          img.rows[i][j].color === dc ? "transparent" : img.rows[i][j].color;
        console.log(context.fillStyle);
        context.fillRect(j * 10, i * 10, 10, 10);
      }
    }
    const url = path.join("..", "src", "assets", "images", "kojima.jpg");
    loadImage(url).then((image) => {
      console.log(image);
      context.drawImage(image, 0, 0);
    });
    // socket.current!.send(
    //   JSON.stringify({
    //     img: art,
    //     cs: settings!.columnSize,
    //     rs: settings!.rowSize,
    //     dc: settings!.defColor,
    //   })
    // );
    // setImg(art);
  };

  return (
    <form onSubmit={onSaveImage}>
      <button type="submit" className={classes.SaveImage}>
        SAVE
      </button>
      {/* <input type="hidden" name="img" value={JSON.stringify(img)} />
      <input type="hidden" name="cs" value={settings!.columnSize} />
      <input type="hidden" name="rs" value={settings!.rowSize} />
      <input type="hidden" name="dc" value={settings!.defColor} /> */}
    </form>
  );
};

export default SaveImage;
