import { useEffect, useRef } from "react";

const UploadImage = () => {
  const socket = useRef<null | WebSocket>(null);

  useEffect(() => {
    const ws = new WebSocket("ws://localhost:3001");
    socket.current = ws;
  }, []);

  const onUploadImage = () => {
    const inp = document.createElement("input");
    inp.type = "file";
    inp.click();
    inp.onchange = (e) => {
      socket.current!.send(
        JSON.stringify((e.target as HTMLInputElement).value)
      );
    };
  };

  return <button onClick={onUploadImage}>Load</button>;
};

export default UploadImage;
