import { useEffect, useRef } from "react";

const UploadImage = () => {
  const socket = useRef<null | WebSocket>(null);

  useEffect(() => {
    const ws = new WebSocket("ws://localhost:3001");
    socket.current = ws;
  }, []);

  const onUploadImage = () => {
    const inp = document.createElement("input");
    inp.type = "file";
    inp.click();
    inp.onchange = (e) => {
      const img = document.createElement("img");
      img.src = URL.createObjectURL((e.target as HTMLInputElement).files![0]);
      socket.current!.send(
        JSON.stringify({
          type: "UPL_IMG",
          data: URL.createObjectURL((e.target as HTMLInputElement).files![0]),
        })
      );
    };
  };

  return (
    <form action="http://localhost:3001/upl-img" method="post">
      <button onClick={onUploadImage}>Load</button>
      <input type="file" name="img" style={{ display: "none" }} />
    </form>
  );
};

export default UploadImage;
