import { useContext, useEffect, useRef } from "react";

import { ArtsContext } from "../../context/arts-context";

const UploadImage = () => {
  const socket = useRef<null | WebSocket>(null);
  const { artsState } = useContext(ArtsContext)!;

  useEffect(() => {
    const ws = new WebSocket("ws://localhost:3001");
    socket.current = ws;
  }, []);

  return (
    <form action="/upl-img" method="post" encType="multipart/form-data">
      {/* <button onClick={onUploadImage}>Load</button> */}
      <input type="file" name="img" />
      <input type="hidden" value={artsState.settings.pixelSize} />
      <button type="submit">upload</button>
    </form>
  );
};

export default UploadImage;
