import classes from "./Board.module.css";

import { SettingsContext } from "../../context/settings-context";
import { ArtsContext } from "../../context/arts-context";
import {
  DragEventHandler,
  MouseEventHandler,
  useContext,
  useEffect,
  useRef,
} from "react";
import { Art } from "../../context/arts-context";

import Pixel from "./Pixel/Pixel";

const Board = () => {
  const { settings } = useContext(SettingsContext);
  const { arts, artsDispatch } = useContext(ArtsContext)!;
  const boardRef = useRef(null);

  useEffect(() => {
    if (!arts.chosen) {
      const board: { xpos: number; ypos: number; color: string }[][] = [];
      for (let i = 0; i < settings!.rowSize; i++) {
        let arr = [];
        for (let j = 0; j < settings!.columnSize; j++) {
          arr.push({ xpos: j, ypos: i, color: settings!.defColor });
        }
        board.push(arr);
      }
      const id = Math.random().toString();
      artsDispatch({
        type: "ADD_ART",
        data: {
          rows: board,
          id: id,
        },
      });
      artsDispatch({ type: "SET_CHOSEN", data: id });
    }
  }, [artsDispatch, settings, arts]);

  const onEndBrush: EventListener = (e) => {
    e.preventDefault();
    document.removeEventListener("dragend", onEndBrush);
  };

  const onStartBrush: DragEventHandler = (e) => {
    const clone = (e.target as HTMLElement).cloneNode(true);
    (clone as HTMLElement).style.display = "none";
    e.dataTransfer.setDragImage(clone as Element, 0, 0);
    document.addEventListener("dragend", onEndBrush);
  };

  const pixelClickedHandler = (xpos: number, ypos: number) => {
    return ((e) => {
      e.preventDefault();
      switch (settings!.tool) {
        case "BRUSH":
          artsDispatch({
            type: "SET_PIX",
            artId: arts.chosen!,
            data: settings!.color,
            x: xpos,
            y: ypos,
          });
          break;
        case "ERASER":
          artsDispatch({
            type: "SET_PIX",
            artId: arts.chosen!,
            data: settings!.defColor,
            x: xpos,
            y: ypos,
          });
        default:
          return;
      }
    }) as MouseEventHandler<HTMLDivElement>;
  };

  let art: null | Art = null;

  if (arts.arts.length) {
    art = { ...arts.arts!.find((art) => art.id === arts.chosen)! };
  }

  const ps = (600 - settings!.columnSize + 1) / settings!.columnSize;

  return (
    <div ref={boardRef} className={classes.Board}>
      {art &&
        art.rows.map((arr) => {
          return (
            <div className={classes.BoardLine} key={art!.rows.indexOf(arr)}>
              {arr.map((pixel) => (
                <Pixel
                  size={ps}
                  clicked={pixelClickedHandler}
                  dragStarted={onStartBrush}
                  key={pixel.ypos.toString() + pixel.xpos}
                  {...pixel}
                />
              ))}
            </div>
          );
        })}
    </div>
  );
};

export default Board;
